import React from "react";

const Blog=(state)=>{

    const {handleChange,items,Addd}=state;


   const DeleteItem=(id)=>{
         items = this.state.items.filter(item=>{
          return item.id !== id
        })
        this.setState({
          items
          })
      }



const man = ()=>{
    return(
    <div className="blog">
    <form onSubmit={Addd(this.state)} type="submit">
        <input type="text" placeholder="Add Course" onChange={handleChange}/>
        <button type="submit">Add Course</button>
    </form>
        </div>
)}


return(
        <div>{man}</div>
)
        
        
 


}
export default Blog;
