import React from "react";
const Blog=(state)=>{

    const {handleChange,handleSubmit,DeleteItem,items}=state;

const callback = items.map(item=>{
  return(

  <div key={item.id}>     <button onClick={()=>DeleteItem(item.id)}>Delete Course</button> 
  </div>
  )
})


  
    return (
        <div key="items.id" className='adding_item'>
   <form type="submit" onSubmit={handleSubmit}>
  
  
   <input type="text" placeholder="enter name" onChange={handleChange} id="name"/>
     <input type="submit" placeholder="submit" />
{callback}
   </form>
   </div>
   
  )      


}
export default Blog;
