import React from "react";
const [input, setInput] = useState([]);

const Blog=(state)=>{
const {items,DeleteItem,handleChange}=state;

  
  const maw = items.map(item=>{
    setInput((input) => input.concat(""));

        return(
      <div key={item.id}>
  <input onChange={handleChange} type="text" placeholder="edit name" />
      </div>
      )
    }
  )
      
   
         
    const man = items.map(item=>{
      return(
<div key={item.id}>
  
     <p> {item.name}</p> 
    
     <button onClick={maw}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
