import React from "react";
const Blog=(state)=>{
const {items,DeleteItem,handleChange}=state;
const edit =items.map((item, e) => {
  return (
    <div key={item.id}>
      <input
        type="text"
        placeholder="Enter text"
        onChange={(e) => handleEdit(item.value, e)}
        value={item.name}
      />
    </div>
  )
})


function handleEdit(i, e) {
  e.preventDefault();

  setInput([
    {
      id: i,
      value: e.target.value
    }
  ]);
}

         
    const man = items.map(item=>{
      return(
<div key={item.id}>
  
     <p> {item.name}</p> 
    
     <button onClick={(e)=>edit(item.name,e)}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
