import React,{Component}  from "react";
import Blog from "./blog";
import Hazem from "./hazem";
class App extends Component{

  state = {
    items:[
{

name:'html'
},
{
name:'css'
},
{
name:'java script'
}
    ]
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id]:e.target.value
    })
}

handleSubmit=(e)=>
{
  e.preventDefault();
this.Addd(this.state);


}






 Addd =(item)=>{

 let items = this.state.items;
  items.push(item);
  this.setState({
  items:items
  })


    }

    DeleteItem =(id)=>{
     let items=this.state.items.filter(item=>{
        return(
       item.id !== id
        )
        })
     this.setState({
       items:items
       })


      }


  render()
  {
const {items}=this.state;
const Hazem = items.map(item=> {
  return
    <Hazem details={item}/>

})

    return(
<section className="App">
  Add Courses
<ul>  <Blog   items={this.state.items}  DeleteItem={this.DeleteItem} />
</ul>
<Hazem handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
</section>
    );
  }
}
export default App;
