  let items = this.state.items;
  [e.target.id]=e.target.value
this.setState({
  items
})

}

render()
{
  return (

<div id="App">
  <h2>Add Courses</h2>

  <blog items = {this.state.items} handleSubmit={this.handleSubmit} />
</div>
 );
}

}
