import React from "react";
const Blog=(state)=>{
const {items,DeleteItem,handleChange,handleSubmit}=state;

  
  const maw = items.map(item=>{

        return(
<div>     
       <form onSubmit={handleSubmit}> 
        <input type="text" placeholder="enter name"  id="name" onChange={handleChange}/>
    <input type="submit" placeholder="submit" />
        </form>

    </div>      )
    }
  )
      
   
         
    const man = items.map(item=>{
      return(
<div key={item.id}>
  
     <p> {item.name}</p> 
    
     <button onClick={maw}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
