import React from "react";
var i=1;
const Blog=(state)=>{

    const {handleChange,handleSubmit,items,DeleteItem}=state;

const man = items.map(item=>{
    return(

    <div key={item.id}>
    <form  type="submit" onSubmit={handleSubmit}>
        <input type="text" placeholder="Add Course" onChange={handleChange}/>
        <button type="submit" onClick={handleSubmit}>Add Course</button>
 <button type="submit" onClick={()=>DeleteItem(item.id)}>Delete Course</button>   

    </form>
        </div>
)})

return(
        <div key={man}>{man}</div>
)

}
export default Blog;
