import React,{Component}  from "react";
import Blog from "./blog";
class App extends Component{
  state = {
    items:[
      {name:'hazem'}
        ]
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id]:e.target.value
    })
}

handleSubmit=(e)=>
{
  e.preventDefault();
this.Addd(this.state);


}


 DeleteItem=(id)=>{
  let items = this.state.items.filter(item=>{
   return item.id !== id
 })
 this.setState({
   items
   })
}


 Addd =(item)=>{
 let items = this.state.items;
  items.push(item);
  this.setState({
  items
  })
return(
<div>
  <form>
  <p>{item.name}</p>
  <button>Edit Course</button>
  <button onClick={this.DeleteItem(item.id)}>Delete Course</button>
  </form>


</div>
)


    }


  render()
  {


    return(
<div className="App">
  Add Courses

<Blog  handleChange={this.handleChange}  handleSubmit={this.handleSubmit} items={this.state.items} />
</div>
    );
  }
}
export default App;
