import React,{Component}  from "react";
import Blog from "./blog";
let i=0;
class App extends Component{

  state = {
    items:[
      {name:'hazem' ,id:1},
      {name:'hazem',id:2} , {name:'hazem',id:3} 

    ]
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id]:e.target.value
    })
}

handleSubmit=(e)=>
{
  e.preventDefault();
this.Addd(this.state);


}

 DeleteItem=(id)=>{
  let items = this.state.items.filter(item=>{
   return item.id !== id
 })

 this.setState({
   items
   })
}


 Addd =(item)=>{

 let items = this.state.items;
  items.push(item);
  this.setState({
  items
  })



    }

  render()
  {


    return(
<div className="App">
  Add Courses

<Blog  handleChange={this.handleChange} handleSubmit={this.handleSubmit} items={this.state.items} DeleteItem={this.DeleteItem} />
</div>
    );
  }
}
export default App;
