import React from "react";
var i=0;
const Blog=(state)=>{

    const {handleChange,handleSubmit,items,DeleteItem,EditItem}=state;

const man = items.map(item=>{
    i++;
item.id=i;
    return(

    <div key="item.id">
    <form  type="submit">
        <input type="text" placeholder="Add Course" onChange={handleChange}/>
        <button type="submit" onClick={handleSubmit}>Add Course</button>
        <button type="submit" onClick={()=>DeleteItem(item.id)}>Delete Course</button>

        <button type="submit" onClick={()=>EditItem(item.id)}>Edit Course</button>
    </form>
        </div>
)})


return(
        <div>{man}</div>
)

}
export default Blog;
