import React from "react";

const Blog=(state)=>{

const {items,DeleteItem,Edit}=state;

Edit =(id)=>{
  
  const maw = items.map(item=>{
    if(item.id===id){
    return(
      <div>
        <form onSubmit={this.handleSubmit}><input onChange={this.handleChange}>{item.name}</input></form>
      </div>
      )
    }
  })
      
      return  (
        <div>{maw}</div>
      )  
       
      }    
    const man = items.map(item=>{
      return(
<div key={item.id}>
  
     <p> {item.name}</p> 
    
     <button onClick={()=>Edit(item.id)}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
