import React,{Component}  from "react";
import Blog from "./blog";
class App extends Component{
  state = {
    items:[
      {name:'hazem'}
        ]
  }
  handleChange=(e)=>{
    [e.target.id]=e.target.value
    this.setState({
      items
    })
}
  render()
  {


    return(
<div className="App">
  Add Courses
<Blog items={this.state.items} handleChange={this.handleChange} />
</div>
    );
  }
}
export default App;
