import React  from "react";
const Blog=(state)=>{
const {items,DeleteItem,CompEdit}=state;

         
    const man = items.map(item=>{
      return(
<div key={item.id}>
  
<div >
  <input type="text">{item.name}</input>
  </div> 
    
     <button onClick={CompEdit}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
