import React from "react";

const Blog=(state)=>{

const {items,DeleteItem}=state;
const Edit =items.map(item=>{

  return(
  <div>
    <form onSubmit={this.handleSubmit}><input onChange={this.handleChange}>{item.name}</input></form>
  </div>
  )

  
  
  })


    const man = items.map(item=>{
      return(
<div key={item.id}>
  
     <p> {item.name}</p> 
    
     <button onClick={Edit}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
