import React from "react";
const Blog=(state)=>{

    const {handleChange,handleSubmit,items,DeleteItem}=state;


    const ml = items.map(item=>{
      return(
      <div key={item.id}>
          {item.name}
  <div key={item.id}>
  <button onClick={DeleteItem}>Delete Course</button> 
  
  </div>
                   
  
      </div>
      )
  })


  
    return (
        <div key="items.id" className='adding_item'>
   <form type="submit" onSubmit={handleSubmit}>
  
  
   <input type="text" placeholder="enter name" onChange={handleChange} id="name"/>
     <input type="submit" placeholder="submit" />
   </form>
   <div>{ml}</div>
   </div>
   
  )      


}
export default Blog;
