import React from "react";

const Blog=(state)=>{

const {items,DeleteItem,handleSubmit,Edit}=state;
const loop=items.map(item=>{
return(

  <div key="item.id" className='adding_item'>

  <form type="submit">
   <div>{item.name}</div> 

 <button onClick={Edit}>Edit Course</button>

    
<button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
  </form>
  </div>

)
}
)
    return (
<div key={loop}>{loop}</div>
   
  )     

}
export default Blog;
