import React from "react";

const Blog=(state)=>{

   const {Addd,handleChange,items}=state;
const man = items.map(item=>{
    return(
    <div className="blog">
    <form onSubmit={Addd(item)} type="submit">
        <input type="text" placeholder="Add Course" onChange={handleChange}/>
        <button type="submit">Add Course</button>
    </form>
        </div>
)}

)
return(
        <div>{man}</div>
)
        
        
 


}
export default Blog;
