import React from "react";

const Blog=(state)=>{

    const {handleChange,items}=state;


    DeleteItem=(id)=>{
        let items = this.state.items.filter(item=>{
          return item.id !== id
        })
        this.setState({
          items
          })
      }

   const Addd =items.map(item=>{
        items.push(item);
        this.setState({
        items
        })
return(
    <div key={item.id}>
        {item.name}
        <button>Edit Course</button>
        <button onClick={()=>DeleteItem(item.id)}>Delete Course</button>

    </div>
)


          })

const man = items.map(item=>{
    return(
    <div className="blog">
    <form onSubmit={Addd} type="submit">
        <input type="text" placeholder="Add Course" onChange={handleChange}/>
        <button type="submit">Add Course</button>
    </form>
        </div>
)}

)
return(
        <div>{man}</div>
)
        
        
 


}
export default Blog;
