import React , { Component } from "react";
class App extends Component{
  state = {
    items:[
    {}
    ]
  }
handleSubmit=(e)=>{
  let items = this.state.items;
  [e.target.id]=e.target.value
  items.splice(e,1);
  this.setState({
    items
  })

}

render()
{
  return (

<div key={items.id}>
  <h2>Add Courses</h2>
  <input type="text"/>
  <button onClick={this.handleSubmit}>Add Course</button>
</div>
 );
}

}
export default App;
