              let items=this.state.items.filter(item=>{
            return(
              item
              )
              })
              this.setState({
items               
})
        }




 Edit =(e)=>{


  this.setState({
[e.target.id]:e.target.value
  })


    }
  render()
  {


    return(
<div className="App">
  Add Courses
  <Hazem   handleChange={this.handleChange} handleSubmit={this.handleSubmit}/>
