import React  from "react";
const Blog=(state)=>{
const {items,DeleteItem,CompEdit,handleChange,handleSubmit}=state;

         
    const man = items.map(item=>{
      return(
<div key={item.id}>
  

  
<div key={item.id}>
    <form onSubmit={handleSubmit}><input type="text" placeholder="edit course" onChange={handleChange}>{item.name}</input>
</form>
  </div> 
    
     <button onClick={CompEdit}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
