import React from "react";
const Hazem = (state)=>{
const{items,handleChange}=state;

const DeleteItem =(id)=>{
    items.filter(item=>{
      return(
     item.id!==id
      )
      })
   this.setState({
     items
     })
    }
const ml = items.map(item=>{
    return(
    <div key={item.id}>
        {item.name}
<div key={item.id}>
<input type="text" placeholder="enter name" onChange={handleChange} id="name"/>
     <input type="submit" placeholder="submit" />   
<button type="submit" onClick={()=>DeleteItem(item.id)}>Delete Course</button> 
</div>
                 

    </div>
    )
})
return(
    <div key={ml}>
{ml}
    </div>
)
}
export default Hazem;
