name:'java script'
}
    ],
    current:''
  }






 Edit =(e)=>{


  this.setState({
current:e.target.value
  })


    }

    DeleteItem =(e)=>{
     let items=this.state.items.filter(item=>{
      return(
       item.id !== e.target.value
        )
        })
     this.setState({
       items
       })


      }


  render()
  {


    return(
<section className="App">
  Add Courses
  <Hazem   />
  <Blog   items={this.state.items}  DeleteItem={this.DeleteItem} Edit={this.Edit}/>

</section>
    );
  }
}
export default App;
