  state = {
    items:[
      {name:'hazem'}
        ]
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id]:e.target.value
    })
}

handleSubmit=(e)=>
{
  e.preventDefault();
this.Addd(this.state);


}


 DeleteItem=(id)=>{
  items = this.state.items.filter(item=>{
   return item.id !== id
 })
 this.setState({
   items
   })
}


 Addd =items.map(item=>{
 let items = this.state.items;
  items.push(item);
  this.setState({
  items
  })
return(
<div key={item.id}>
  {item.name}
  <button>Edit Course</button>
  <button onClick={()=>DeleteItem(item.id)}>Delete Course</button>

</div>
)


    })


  render()
  {


    return(
<div className="App">
  Add Courses

<Blog  handleChange={this.handleChange} items={this.state.items} handleSubmit={this.handleSubmit} />
</div>
    );
  }
}
export default App;
