const Blog=(state)=>{

    const{items,DeleteItem,Addd,handleChange,handleSubmit}= this.state;
    Addd = (item)=>{
        let items = this.state.items;
        items.push(item);
        this.setState({
        items
        })
          }
const Acd=items.map(item=>{

return(
<div key={item.id}>
    <div>{item.name}</div>
<button onClick={()=>DeleteItem(item.id)} className='Btn'>DeleteBtn</button>

</div>

)
})



return
(

        
        <div className="Addcourse">
    <form onSubmit={Acd}>
        <input type="text" placeholder="Add Course" onChange={handleChange}/>
        <button type="submit">Add Course</button>
    </form>
        </div>
        
        
 

)
}
export default Blog;
