import React from "react";

const Blog=(state)=>{

    const{items,handleChange}= this.state.items;


const AddItem = items.map(item=>{

return(



<div>{item.name}</div>




)
})

return
(

    <div>
<form onSubmit={AddItem}>
    <input type="text" placeholder="Add Course" onChange={handleChange}/>
    <button type="submit">Add Course</button>
</form>
    </div>
)
}
export default Blog;
