class Hazem extends Component{
    
    state = {
        items:[
    {
    
    name:'html'
    },
    {
    name:'css'
    },
    {
    name:'java script'
    }
        ],
        current:''
      }
      
    
    
    
    
    
    handleChange=(e)=>{
        let current = this.state.current;

        this.setState({
          [e.target.id]:e.target.value
        })
    }
    
    handleSubmit=(e)=>
    {
      e.preventDefault();
      let items = this.state.items;
      let current = this.state.current;
      items.push({name:current});
      this.setState({
        items,
        current:''
      })
    
    
    }
    


