return(
<div>        <form onSubmit={this.handleSubmit}> 
        <input type="text" placeholder="enter name" onChange={this.handleChange} id="name"/>
    <input type="submit" placeholder="submit" />
        </form>

    </div>
);
}
}
export default Hazem;
