import React from "react";
import Hazem from "./hazem";
const Blog=(state)=>{
const {items,DeleteItem}=state;

function edit(i,e){
  e.preventDefault();

  setInput([
    {
      id: i,
      value: e.target.value
    }
  ]);
}   
   
         
    const man = items.map(item=>{
      return(
<div key={item.id}>
  
     <p> {item.name}</p> 
    
     <button onClick={edit(item.id,e)}>Edit Course</button>
    
        
    <button onClick={()=>DeleteItem(item.id)}>Delete Item</button>
      </div>
       )

      })
  return(
<div key={items.id}>
  {man}
</div>
  )     

}
export default Blog;
